if [ ... ]; then ... elif [ ... ]; then ... else ... fi  demo如下
#!/bin/bash #test if else #-f ----&gt; if is a file a=&quot;if-else.sh&quot; if [ -f $a ]; then echo &quot;$a is a file&quot; else echo &quot;$a is not a file&quot; fi #-lt ----&gt; less than -le -&gt; less equal b=4 if [ $b -lt 5 ]; then echo &quot;$b is less than 5&quot; else echo &quot;$b is great or equal than 5&quot; fi #-x ----&gt; if is executable c=&quot;if-else.
